import React, { useEffect, useState } from "react";
import styles from "./Login.module.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setpassword] = useState("");
  const navigate = useNavigate();
  const [show, setShow] = useState(false);
  const errMsg = <p className={styles.err}>Enter Valid Username / Password</p>;

  const usernameHandler = (e) => {
    setUsername(e.target.value);
    setShow(false);
  };

  const passwordHandler = (e) => {
    setpassword(e.target.value);
    setShow(false);
  };

  useEffect(() => {
    const getData = async () => {
      try {
        const data = await axios.localStorage.getItem("loginStatus");
        console.log(data);
      } catch (err) {
        console.log(err);
      }
    };
    getData();
  }, []);

  const loginHandler = (e) => {
    e.preventDefault();
    if (username === password && username !== "" && password !== "") {
      setShow(false);
      localStorage.setItem("loginStatus", true);
      navigate("/customer");
      console.log("login");
    } else {
      setShow(true);
    }
  };

  return (
    <div className={styles.logincontainer}>
      <div>
        <form onSubmit={loginHandler}>
          <h2 className="text-center">Welcome to Dashboard, Login</h2>
          <label>Username</label>
          <input type="text" onChange={usernameHandler} value={username} />
          <label>Password</label>
          <input type="password" onChange={passwordHandler} value={password} />
          {show && errMsg}
          <button type="submit" className="btn">
            Login
          </button>

          <br />
        </form>
      </div>
    </div>
  );
};

export default Login;
